import { MachineContext } from './pressable.types.js';
import '@zag-js/core';
import '@zag-js/types';

declare const dom: {
    getRootNode: (ctx: {
        getRootNode?: (() => Document | Node | ShadowRoot) | undefined;
    }) => Document | ShadowRoot;
    getDoc: (ctx: {
        getRootNode?: (() => Document | Node | ShadowRoot) | undefined;
    }) => Document;
    getWin: (ctx: {
        getRootNode?: (() => Document | Node | ShadowRoot) | undefined;
    }) => Window & typeof globalThis;
    getActiveElement: (ctx: {
        getRootNode?: (() => Document | Node | ShadowRoot) | undefined;
    }) => HTMLElement | null;
    getById: <T extends HTMLElement = HTMLElement>(ctx: {
        getRootNode?: (() => Document | Node | ShadowRoot) | undefined;
    }, id: string) => T | null;
    queryById: <T_1 extends HTMLElement = HTMLElement>(ctx: {
        getRootNode?: (() => Document | Node | ShadowRoot) | undefined;
    }, id: string) => T_1;
} & {
    getPressableId: (ctx: MachineContext) => string;
    getPressableEl: (ctx: MachineContext) => HTMLElement | null;
};

export { dom };
